function checkboxFocus(event) {
  event.currentTarget.parentNode.classList.add('focus');
}

function checkboxBlur(event) {
  event.currentTarget.parentNode.classList.remove('focus');
}

class MenuButtonLinks {

  constructor(domNode) {

    this.domNode       = domNode;
    this.buttonNode    = domNode.querySelector('button');
    this.menuNode      = domNode.querySelector('ul');

    this.buttonNode.addEventListener('click', this.onButtonClick.bind(this));
    this.buttonNode.addEventListener('blur', this.onBlur.bind(this));

    const links = domNode.querySelectorAll('a');
    for (let i = 0; i < links.length; i += 1) {
      links[i].addEventListener('blur', this.onBlur.bind(this));
    }
    window.addEventListener('mousedown', this.onBackgroundMousedown.bind(this), true);
  }

  // Popup menu methods

  openPopup() {
    var rect = this.menuNode.getBoundingClientRect();
    this.menuNode.style.display = 'block';
    this.buttonNode.classList.add('open');
  }

  closePopup() {
    if (this.isOpen()) {
      this.buttonNode.classList.remove('open');
      this.menuNode.style.display = 'none';
    }
  }

  isOpen() {
    return this.buttonNode.classList.contains('open');
  }

  onButtonClick(event) {
    if (this.isOpen()) {
      this.closePopup();
    }
    else {
      this.openPopup();
    }

    event.stopPropagation();
    event.preventDefault();
  }

  onBlur(event) {
    if (!this.domNode.contains(event.relatedTarget)) {
      if (this.isOpen()) {
        this.closePopup();
      }
    }
  }

  onBackgroundMousedown(event) {
    if (!this.domNode.contains(event.target)) {
      if (this.isOpen()) {
        this.closePopup();
        this.buttonNode.focus();
      }
    }
  }
}

// Initialize navigation menu buttons

window.addEventListener('load', function () {
  var menuButtons = document.querySelectorAll('.menu-button-links');
  for(var i=0; i < menuButtons.length; i++) {
    var menuButton = new MenuButtonLinks(menuButtons[i]);
  }
});

class MenuButtonLinks {

  constructor(domNode) {

    this.domNode       = domNode;
    this.buttonNode    = domNode.querySelector('button');
    this.menuNode      = domNode.querySelector('.menu');
    this.menuitemNodes = []
    this.firstMenuitem = false;
    this.lastMenuitem  = false;
    this.firstChars = [];

    this.buttonNode.addEventListener('keydown', this.onButtonKeydown.bind(this));
    this.buttonNode.addEventListener('click', this.onButtonClick.bind(this));

    var nodes = this.menuNode.querySelectorAll('a');

    for (var i = 0; i < nodes.length; i++) {
      var menuitem = nodes[i];
      this.menuitemNodes.push(menuitem);
      menuitem.tabIndex = -1;
      this.firstChars.push(menuitem.textContent.trim()[0].toLowerCase());

      menuitem.addEventListener('keydown', this.onMenuitemKeydown.bind(this));

      menuitem.addEventListener('mouseover', this.onMenuitemMouseover.bind(this));

      if( !this.firstMenuitem) {
        this.firstMenuitem = menuitem;
      }
      this.lastMenuitem = menuitem;
    }

    domNode.addEventListener('focusin', this.onFocusin.bind(this));
    domNode.addEventListener('focusout', this.onFocusout.bind(this));

    window.addEventListener('mousedown', this.onBackgroundMousedown.bind(this), true);
  }

  // Popup menu methods

  openPopup() {
    var rect = this.menuNode.getBoundingClientRect();
    this.menuNode.style.display = 'block';
    this.buttonNode.classList.add('open');
  }

  closePopup() {
    if (this.isOpen()) {
      this.buttonNode.classList.remove('open');
      this.menuNode.style.display = 'none';
    }
  }

  isOpen() {
    return this.buttonNode.classList.contains('open');
  }

  // Focus management methods

  setFocusToMenuitem(newMenuitem) {
    this.menuitemNodes.forEach(function(item) {
      if (item === newMenuitem) {
        item.tabIndex = 0;
        newMenuitem.focus();
      }
      else {
        item.tabIndex = -1;
      }
    });
  }

  setFocusToFirstMenuitem(currentMenuitem) {
    this.setFocusToMenuitem(this.firstMenuitem);
  }

  setFocusToLastMenuitem(currentMenuitem) {
    this.setFocusToMenuitem(this.lastMenuitem);
  }

  setFocusToPreviousMenuitem(currentMenuitem) {
    var newMenuitem, index;

    if (currentMenuitem === this.firstMenuitem) {
      newMenuitem = this.lastMenuitem;
    }
    else {
      index = this.menuitemNodes.indexOf(currentMenuitem);
      newMenuitem = this.menuitemNodes[ index - 1 ];
    }

    this.setFocusToMenuitem(newMenuitem);

    return newMenuitem;
  }

  setFocusToNextMenuitem(currentMenuitem) {
    var newMenuitem, index;

    if (currentMenuitem === this.lastMenuitem) {
      newMenuitem = this.firstMenuitem;
    }
    else {
      index = this.menuitemNodes.indexOf(currentMenuitem);
      newMenuitem = this.menuitemNodes[ index + 1 ];
    }
    this.setFocusToMenuitem(newMenuitem);

    return newMenuitem;
  }

  setFocusByFirstCharacter(currentMenuitem, char) {
    var start, index;

    if (char.length > 1) {
      return;
    }

    char = char.toLowerCase();

    // Get start index for search based on position of currentItem
    start = this.menuitemNodes.indexOf(currentMenuitem) + 1;
    if (start >=  this.menuitemNodes.length) {
      start = 0;
    }

    // Check remaining slots in the menu
    index = this.firstChars.indexOf(char, start);

    // If not found in remaining slots, check from beginning
    if (index === -1) {
      index = this.firstChars.indexOf(char, 0);
    }

    // If match was found...
    if (index > -1) {
      this.setFocusToMenuitem(this.menuitemNodes[index]);
    }
  }

  // Utilities

  getIndexFirstChars(startIndex, char) {
    for (var i = startIndex; i < this.firstChars.length; i++) {
      if (char === this.firstChars[i]) {
        return i;
      }
    }
    return -1;
  }


  // Menu event handlers

  onFocusin(event) {
    this.domNode.classList.add('focus');
  }

  onFocusout(event) {
    this.domNode.classList.remove('focus');
  }

  onButtonKeydown(event) {
    var tgt = event.currentTarget,
      key = event.key,
      flag = false;

    switch (key) {
      case ' ':
      case 'Enter':
      case 'ArrowDown':
      case 'Down':
        this.openPopup();
        this.setFocusToFirstMenuitem();
        flag = true;
       break;

      case 'Esc':
      case 'Escape':
          this.closePopup();
          this.buttonNode.focus();
          flag = true;
        break;

      case 'Up':
      case 'ArrowUp':
        this.openPopup();
        this.setFocusToLastMenuitem();
        flag = true;
        break;

      default:
        break;
    }

    if (flag) {
      event.stopPropagation();
      event.preventDefault();
    }
  }

  onButtonClick(event) {
    if (this.isOpen()) {
      this.closePopup();
      this.buttonNode.focus();
    }
    else {
      this.openPopup();
      this.setFocusToFirstMenuitem();
    }

    event.stopPropagation();
    event.preventDefault();
  }

  onMenuitemKeydown(event) {
    var tgt = event.currentTarget,
      key = event.key,
      flag = false;

    function isPrintableCharacter (str) {
      return str.length === 1 && str.match(/\S/);
    }

    if (event.ctrlKey || event.altKey  || event.metaKey) {
      return;
    }

    if (event.shiftKey) {
      if (isPrintableCharacter(key)) {
        this.setFocusByFirstCharacter(tgt, key);
        flag = true;
      }

      if (event.key === 'Tab') {
        this.buttonNode.focus();
        this.closePopup();
        flag = true;
      }
    }
    else {

      switch (key) {
        case ' ':
         window.location.href=tgt.href;
         break;

        case 'Esc':
        case 'Escape':
          this.closePopup();
          this.buttonNode.focus();
          flag = true;
          break;

        case 'Up':
        case 'ArrowUp':
          this.setFocusToPreviousMenuitem(tgt);
          flag = true;
          break;

        case 'ArrowDown':
        case 'Down':
          this.setFocusToNextMenuitem(tgt);
          flag = true;
          break;

        case 'Home':
        case 'PageUp':
          this.setFocusToFirstMenuitem();
          flag = true;
          break;

        case 'End':
        case 'PageDown':
          this.setFocusToLastMenuitem();
          flag = true;
          break;

        case 'Tab':
          this.closePopup();
          break;

        default:
          if (isPrintableCharacter(key)) {
            this.setFocusByFirstCharacter(tgt, key);
            flag = true;
          }
          break;
      }

    }

    if (flag) {
      event.stopPropagation();
      event.preventDefault();
    }
  }

  onMenuitemMouseover(event) {
    var tgt = event.currentTarget;
    tgt.focus();
  }

  onBackgroundMousedown(event) {
    if (!this.domNode.contains(event.target)) {
      if (this.isOpen()) {
        this.closePopup();
        this.buttonNode.focus();
      }
    }
  }
}

// Initialize navigation menu buttons

