const age = [
  [18, 22],
  [13, 16],
  [ 9, 10],
  [ 1,  1],
  [ 0,  0]
];

const pdqResult = [
  [25, '5-25% chance', 'Below average'],
  [36, '25-35% chance', 'Average'],
  [50, '35-50% chance', 'Above average'],
  [200, '50% or more chance', 'Significantly above average']
];

function getValue (name) {
  const radioNodes = document.querySelectorAll(`[name=${name}]`);
  for (let i = 0; i < radioNodes.length; i += 1 ) {
    if (radioNodes[i].checked) {
      console.log(`[${name}Value]: ${radioNodes[i].value}`);
      return parseInt(radioNodes[i].value);
    }
  }
  return 0;
}

function computePDQ() {

  console.log(`=== Compute ===`);
  const genderValue = getValue('gender');
  const ageValue = getValue('age');

  let pdq = age[ageValue][genderValue];

  pdq += getValue('activity');
  pdq += getValue('bmi');
  pdq += getValue('tabacco');
  pdq += getValue('life');
  pdq += getValue('mc');

  console.log(`[pdq]: ${pdq}`);

  let prop   = pdqResult[0][1];
  let likely = pdqResult[0][2];

  for (let i = 0; i < pdqResult.length; i += 1) {
    prop   = pdqResult[i][1];
    likely = pdqResult[i][2];
    if (pdq < pdqResult[i][0]) {
      break;
    }
  }

  const pdqCell = document.querySelector('#pdq-value');
  pdqCell.textContent = pdq.toString();

  const propCell = document.querySelector('#pdq-prop');
  propCell.textContent = prop;

  const likelyCell = document.querySelector('#pdq-likely');
  likelyCell.textContent = likely;

}

